package Basic;

public class UpperCaseAndLowerCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1 = "UPPER CASE TO LOWER CASE";
		
		System.out.println("UPPER CASE TO LOWER CASE.....-> "+ str1.toLowerCase());
		
		System.out.println("lower case to upper case... -> "+str1.toUpperCase());

	}

}


