package StringMethods;

public class charAt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str ="sivaramakrishna";
		
		
		System.out.println("r - position at "+ str.charAt(4));
		
		//int index = str.indexOf('r');
        System.out.println("Index of 'r': " + str.indexOf('h'));
        
        
        System.out.println("Index of Last index of a': " + str.lastIndexOf('a'));
        
        System.out.println("Trim of  " + str.trim());
		

	}

}
